import 'package:test/test.dart';
import 'package:codewars_dart/8kyu/dna_to_rna_conversion.dart';

void main() {
  group('Fixed tests', () {
    void testing(String dna, String exp) => test(
          'Testing for $dna',
          () => expect(rnaToDna(dna), equals(exp)),
        );
    testing('TTTT', 'UUUU');
    testing('GCAT', 'GCAU');
    testing('', '');
    testing('T', 'U');
    testing('GACCGCCGCC', 'GACCGCCGCC');
    testing(
      'GATTCCACCGACTTCCCAAGTACCGGAAGCGCGACCAACTCGCACAGC',
      'GAUUCCACCGACUUCCCAAGUACCGGAAGCGCGACCAACUCGCACAGC',
    );
    testing(
      'CACGACATACGGAGCAGCGCACGGTTAGTACAGCTGTCGGTGAACTCCATGACA',
      'CACGACAUACGGAGCAGCGCACGGUUAGUACAGCUGUCGGUGAACUCCAUGACA',
    );
    testing(
      'CACGACATACGGAGCAGCGCACGGTTAGTACAGCTGTCGGTGAACTCCATGACA',
      'CACGACAUACGGAGCAGCGCACGGUUAGUACAGCUGUCGGUGAACUCCAUGACA',
    );
    testing(
      'AACCCTGTCCACCAGTAACGTAGGCCGACGGGAAAAATAAACGATCTGTCAATG',
      'AACCCUGUCCACCAGUAACGUAGGCCGACGGGAAAAAUAAACGAUCUGUCAAUG',
    );
    testing(
      '',
      '',
    );
  });
}
